  { id: 1, name: 'Priya Mehta', message: 'Hello, Happy...', date: 'May 21', unread: 2, avatar: 'ðŸ‘©' },
  { id: 2, name: 'Joshan', message: 'Hello, Happy...', date: 'May 21', avatar: 'ðŸ§”' },
  { id: 3, name: 'Sara Thomas', message: 'Hello, Happy...', date: 'May 21', avatar: 'ðŸ‘©â€ðŸ’¼' },
  { id: 4, name: 'Jason Lee', message: 'Hello, Happy...', date: 'May 21', avatar: 'ðŸ‘¨â€ðŸ’¼' },
];

const Sidebar = () => {
  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h3>Messages</h3>
        <button className="close-btn">âœ–</button>
      </div>
      <input type="text" placeholder="Search chats" className="chat-search" />
      <div className="chat-list">
        {mockChats.map((chat) => (
          <div key={chat.id} className="chat-card">
            <div className="avatar">{chat.avatar}</div>
            <div className="chat-info">
              <div className="chat-name">{chat.name}</div>
              <div className="chat-msg">{chat.message}</div>
            </div>
            <div className="chat-meta">
              <span>{chat.date}</span>
              {chat.unread && <span className="unread-badge">{chat.unread}</span>}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Sidebar;
