import React, { useState } from 'react';
import Sidebar from './Sidebar';
import ChatWindow from './ChatWindow';
import './chat.css';

const mockChats = [
  {
    id: 1,
    name: 'Priya Mehta',
    avatar: '👩',
    messages: [
      { id: 1, sender: 'other', text: "Hi Priya, I came across your startup..." },
      { id: 2, sender: 'me', text: "Hi John! I appreciate that..." },
    ]
  },
  {
    id: 2,
    name: 'Jason Lee',
    avatar: '👨‍💼',
    messages: [
      { id: 1, sender: 'other', text: "Hello, Priya!" },
      { id: 2, sender: 'me', text: "Hey Jason!" }
    ]
  }
];

const ChatApp = () => {
  const [selectedChat, setSelectedChat] = useState(mockChats[0]);
  const [messages, setMessages] = useState(mockChats[0].messages);

  const handleUserSelect = (chat) => {
    setSelectedChat(chat);
    setMessages(chat.messages);
  };

  const handleSendMessage = (text) => {
    const newMessage = {
      id: Date.now(),
      sender: 'me',
      text
    };
    const updatedMessages = [...messages, newMessage];
    setMessages(updatedMessages);
    setSelectedChat({ ...selectedChat, messages: updatedMessages });
  };

  return (
    <div className="chat-container">
      <Sidebar chats={mockChats} onSelectUser={handleUserSelect} />
      <ChatWindow
        user={selectedChat}
        messages={messages}
        onSendMessage={handleSendMessage}
      />
    </div>
  );
};

export default ChatApp;
