import { useState } from 'react';

const MessageInput = () => {
  const [msg, setMsg] = useState('');

  return (
    <div className="message-input-box">
      <input
        type="text"
        placeholder="Message"
        value={msg}
        onChange={(e) => setMsg(e.target.value)}
      />
    </div>
  );
};

export default MessageInput;
