import React, { useState, useRef } from 'react';
import { FiFileText, FiVideo, FiImage } from 'react-icons/fi';

const IdeaModal = ({ onClose, onPost }) => {
  const [title, setTitle] = useState('');
  const [descr, setDescr] = useState('');
  const [fundGoal, setFundGoal] = useState('');
  const [showPreview, setShowPreview] = useState(false);

  const [tags, setTags] = useState([]);
  const [inputTag, setInputTag] = useState('');
  const [files, setFiles] = useState({ pitch: null, video: null, image: null });

  const pitchRef = useRef(null);
  const videoRef = useRef(null);
  const imageRef = useRef(null);

  const handleAddTag = () => {
    const t = inputTag.trim();
    if (t && !tags.includes(t)) setTags([...tags, t]);
    setInputTag('');
  };

  const handleRemoveTag = t => setTags(tags.filter(tag => tag !== t));

  const handleFileSelect = (e, type) => {
    const file = e.target.files[0];
    if (!file) return;

    const validMap = {
      pitch: ['application/pdf','application/msword','application/vnd.openxmlformats-officedocument.wordprocessingml.document'],
      video: ['video/mp4','video/webm','video/ogg'],
      image: ['image/png','image/jpeg','image/jpg','image/webp'],
    };

    if (!validMap[type]?.includes(file.type)) {
      alert(`Invalid ${type} file.`);
      return;
    }

    setFiles(prev => ({ ...prev, [type]: file }));
  };

  const handleSubmit = e => {
    e.preventDefault();
    if (!title) return alert('Title is required');
    onPost({ title, descr, fundGoal, tags, files });
    onClose();
  };

  return (
    <div className="fixed inset-0 bg-black/40 z-50 flex items-start justify-center px-4 pt-6">
      <div className="bg-white rounded-2xl p-4 sm:p-6 w-full max-w-lg animate-fade-in-up shadow-xl border border-purple-200 relative overflow-visible">
        <button onClick={onClose} className="absolute top-3 right-4 text-purple-600 hover:text-purple-800">&times;</button>
        <h2 className="text-center text-2xl font-bold text-primary mb-4">Share your Startup Idea</h2>

        {/* Preview Mode */}
        {showPreview ? (
          <div className="space-y-4">
            <h3 className="font-semibold">Title:</h3><p>{title}</p>
            <h3 className="font-semibold">Description:</h3><p>{descr}</p>
            <h3 className="font-semibold">Tags:</h3><p>{tags.map(t => `#${t}`).join(' ')}</p>
            <h3 className="font-semibold">Files:</h3>
            {files.pitch && <p>üìÑ {files.pitch.name}</p>}
            {files.video && <p>üìπ {files.video.name}</p>}
            {files.image && <p>üñºÔ∏è {files.image.name}</p>}
            <button onClick={() => setShowPreview(false)} className="text-sm text-primary hover:underline">Edit</button>
          </div>
        ) : (
          <form className="space-y-4 text-sm text-primary" onSubmit={handleSubmit}>
            <div>
              <label className="block font-semibold mb-1">Idea Title</label>
              <input className="input text-black" value={title} onChange={e => setTitle(e.target.value)} placeholder="Startup name or concept"/>
            </div>
            <div>
              <label className="block font-semibold mb-1">Description</label>
              <textarea className="input text-black" rows={4} value={descr} onChange={e => setDescr(e.target.value)} placeholder="Describe your idea..."/>
            </div>
            {/* ...tags, files as before */}
            <div className="flex gap-4 pt-4">
              <button type="submit" className="bg-primary text-white px-6 py-2 rounded-full font-semibold">Post Idea</button>
              <button type="button" onClick={() => setShowPreview(true)} className="border-2 border-purple-300 px-6 py-2 rounded-full font-semibold text-primary">Preview</button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
};

export default IdeaModal;
