import ChatHeader from './ChatHeader';
import MessageBubble from './MessageBubble';
import MessageInput from './MessageInput';

const mockMessages = [
  { id: 1, sender: 'other', text: "Hi Priya, I came across your startup..." },
  { id: 2, sender: 'me', text: "Hi John! I appreciate that. I'll be glad to connect..." },
  { id: 3, sender: 'other', text: "Perfect. Just reviewed the brief..." },
  { id: 4, sender: 'me', text: "Yes, we currently have 1,200 beta users..." },
  { id: 5, sender: 'other', text: "Impressive! What's the current funding ask..." },
  { id: 6, sender: 'me', text: "We're raising $40K. 60% for product dev..." },
  { id: 7, sender: 'other', text: "Great. I just booked a slot..." },
  { id: 8, sender: 'me', text: "Looking forward to it." }
];

const ChatWindow = () => {
  return (
    <div className="chat-window">
      <ChatHeader />
      <div className="messages-list">
        {mockMessages.map((msg) => (
          <MessageBubble key={msg.id} sender={msg.sender} text={msg.text} />
        ))}
      </div>
      <MessageInput />
    </div>
  );
};

export default ChatWindow;
