const MessageBubble = ({ message }) => {
  const isMe = message.sender === 'me';

  return (
    <div className={`message-bubble ${isMe ? 'me' : 'other'}`}>
      <p>{message.text}</p>
    </div>
  );
};

export default MessageBubble;
