import React, { useState, useRef } from 'react';
import { FiFileText, FiVideo, FiImage } from 'react-icons/fi';

const IdeaModal = ({ onClose, onPost }) => {
  const [tags, setTags] = useState([]);
  const [inputTag, setInputTag] = useState('');
  const [files, setFiles] = useState({ pitch: null, video: null, image: null });

  const pitchRef = useRef(null);
  const videoRef = useRef(null);
  const imageRef = useRef(null);

  const handleAddTag = () => {
    const trimmed = inputTag.trim();
    if (trimmed && !tags.includes(trimmed)) {
      setTags([...tags, trimmed]);
    }
    setInputTag('');
  };

  const handleRemoveTag = (tagToRemove) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
  };

  const handleFileSelect = (e, type) => {
    const file = e.target.files[0];
    if (!file) return;

    const typeMap = {
      pitch: ['application/pdf', 'application/msword', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'],
      video: ['video/mp4', 'video/webm', 'video/ogg'],
      image: ['image/png', 'image/jpeg', 'image/jpg', 'image/webp'],
    };

    if (!typeMap[type]?.includes(file.type)) {
      alert(`Please upload a valid ${type === 'pitch' ? 'PDF or DOC' : type} file.`);
      return;
    }

    setFiles(prev => ({ ...prev, [type]: file }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add validation checks here if needed

    const postData = { tags, files };
    onPost(postData);
    onClose();
  };

  return (
    <div className="fixed inset-0 bg-black/40 z-50 flex items-start justify-center px-4 pt-6">
      <div className="bg-white rounded-2xl p-6 w-full max-w-2xl animate-fade-in-up shadow-xl border border-purple-200 relative overflow-visible">
        <button onClick={onClose} className="absolute top-3 right-3 text-purple-600 text-lg font-bold hover:text-purple-800">&times;</button>
        <h2 className="text-center text-2xl font-bold text-primary mb-6">Share your Startup Idea</h2>
        <form onSubmit={handleSubmit} className="space-y-4 text-primary text-sm">

          {/* Add Tags */}
          <div>
            <label className="block font-semibold mb-1">Add Tags</label>
            <div className="flex gap-2 mb-2">
              <input
                type="text"
                value={inputTag}
                onChange={e => setInputTag(e.target.value)}
                placeholder="e.g. FinTech"
                className="flex-1 px-3 py-1 rounded-full border border-purple-200 text-xs outline-none text-black"
              />
              <button type="button" onClick={handleAddTag}
                className="text-xs px-3 py-1 rounded-full bg-purple-100 text-primary font-semibold">
                + Add
              </button>
            </div>
            <div className="flex gap-2 flex-wrap">
              {tags.map((tag, i) => (
                <span key={i} className="bg-purple-100 text-primary px-3 py-1 text-xs rounded-full font-medium flex items-center gap-2">
                  #{tag}
                  <button type="button" onClick={() => handleRemoveTag(tag)}
                    className="text-[10px] text-purple-600 hover:text-purple-900">&times;</button>
                </span>
              ))}
            </div>
          </div>

          {/* Attachments */}
          <div>
            <label className="block font-semibold mb-1">Attachments</label>
            <div className="flex flex-wrap gap-3">
              {['pitch','video','image'].map(type => {
                const refMap = { pitch: pitchRef, video: videoRef, image: imageRef };
                const labelMap = {
                  pitch: ['📄', 'Upload Pitch Deck', '.pdf,.doc,.docx'],
                  video: ['🎥', 'Upload Demo Video', 'video/*'],
                  image: ['🖼️', 'Upload Image', 'image/*']
                };
                return (
                  <React.Fragment key={type}>
                    <button type="button" onClick={() => refMap[type].current.click()}
                      className="flex items-center gap-2 border-2 border-purple-200 px-4 py-2 rounded-full text-xs font-medium hover:bg-purple-50">
                      {labelMap[type][0]} {labelMap[type][1]}
                    </button>
                    <input type="file" accept={labelMap[type][2]} ref={refMap[type]}
                      onChange={e => handleFileSelect(e, type)} className="hidden" />
                  </React.Fragment>
                );
              })}
            </div>
          </div>

          {/* Footer Buttons */}
          <div className="flex flex-col sm:flex-row sm:justify-between gap-3 pt-4">
            <button type="submit" className="bg-primary text-white px-6 py-2 rounded-full font-semibold">
              Post Idea
            </button>
            <button type="button" className="border-2 border-purple-300 px-6 py-2 rounded-full font-semibold text-primary">
              Preview
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default IdeaModal;
