import React, { useState } from 'react';
import Sidebar from './Sidebar';
import ChatWindow from './ChatWindow';
import './chat.css';

const mockChats = [
  {
    id: 1,
    name: 'John Doe',
    avatar: '/avatars/john.jpg',
    messages: [
      {
        id: 1,
        sender: 'other',
        text: 'Hi Priya, I came across your startup "NavFlow"...',
        timestamp: '9:10 AM'
      },
      {
        id: 2,
        sender: 'me',
        text: 'Hi John! I appreciate that. I’d be glad to connect...',
        timestamp: '9:10 AM'
      },
    ]
  },
  // Add more chats...
];

const ChatApp = () => {
  const [selectedChat, setSelectedChat] = useState(null);

  const handleUserSelect = (chat) => {
    setSelectedChat(chat);
  };

  return (
    <div className="chat-container">
      <Sidebar chats={mockChats} onSelectUser={handleUserSelect} />
      {selectedChat ? (
        <ChatWindow chat={selectedChat} />
      ) : (
        <div className="empty-chat-window">
          <p>Select a conversation to start chatting</p>
        </div>
      )}
    </div>
  );
};

export default ChatApp;
