import React from 'react';
import logo from '../assets/images/main-logo.png';
import founderIcon from '../assets/images/founder-icon.png';
import investorIcon from '../assets/images/investor-icon.png';

const RoleSelection = () => {
  return (
    <div className="min-h-screen bg-[#fceeff] flex flex-col items-center justify-center px-4 text-center">
      {/* Logo */}
      <img src={logo} alt="The Founders Startup Logo" className="w-40 mb-6" />

      {/* Title */}
      <h1 className="text-3xl md:text-4xl font-bold text-purple-700 mb-4">
        Welcome to The Founders Startup
      </h1>

      {/* Subtitle */}
      <p className="text-md md:text-lg text-purple-700 mb-10 max-w-xl">
        Connect with student innovators and real investors. Pitch your idea or discover the next big thing!
      </p>

      {/* Role Selection */}
      <div className="flex flex-col md:flex-row gap-6">
        {/* Founder */}
        <button className="relative bg-white border-2 border-purple-500 rounded-xl p-6 w-64 transition hover:scale-105 group overflow-hidden shadow-md">
  {/* Glow gradient corners */}
  <div className="absolute top-0 right-0 w-16 h-16 bg-gradient-to-br from-purple-100 to-transparent rounded-bl-full blur-md opacity-70" />
  <div className="absolute bottom-0 left-0 w-16 h-16 bg-gradient-to-tl from-purple-100 to-transparent rounded-tr-full blur-md opacity-70" />

  <img src={founderIcon} alt="Founder Icon" className="w-12 h-12 mx-auto mb-3 relative z-10" />
  <span className="text-lg font-semibold relative z-10">As a Founder</span>
</button>


        {/* Investor */}
        <button className="bg-white border-2 border-purple-500 rounded-xl p-6 w-42 hover:scale-105 transition">
          <img src={investorIcon} alt="Investor Icon" className="w-12 h-12 mx-auto mb-3" />
          <span className="text-lg font-semibold">As an Investor</span>
        </button>
      </div>
    </div>
  );
};

export default RoleSelection;
