const Sidebar = ({ chats, onSelectUser }) => {
  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h3>Messages</h3>
        <button className="close-btn">âœ–</button>
      </div>
      <input type="text" placeholder="Search chats" className="chat-search" />
      <div className="chat-list">
        {chats.map((chat) => (
          <div key={chat.id} className="chat-card" onClick={() => onSelectUser(chat)}>
            <div className="avatar">{chat.avatar}</div>
            <div className="chat-info">
              <div className="chat-name">{chat.name}</div>
              <div className="chat-msg">{chat.messages[chat.messages.length - 1]?.text}</div>
            </div>
            <div className="chat-meta">
              <span>May 21</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Sidebar;
